func (lcb *LiveCreateBulk) OnConflictColumns(columns ...string) *LiveUpsertBulk {
	lcb.conflict = append(lcb.conflict, sql.ConflictColumns(columns...))
	return &LiveUpsertBulk{
		create: lcb,
	}
}

